import type { Express } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import { 
  insertStudentSchema, 
  insertClassSchema, 
  insertAttendanceRecordSchema,
  insertBehaviorWarningSchema,
  insertFaceDetectionSchema,
  insertTeacherSchema
} from "@shared/schema";

export async function registerRoutes(app: Express): Promise<Server> {
  // Students routes
  app.get("/api/students", async (req, res) => {
    try {
      const students = await storage.getStudents();
      res.json(students);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch students" });
    }
  });

  app.get("/api/students/:id", async (req, res) => {
    try {
      const student = await storage.getStudent(req.params.id);
      if (!student) {
        return res.status(404).json({ message: "Student not found" });
      }
      res.json(student);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch student" });
    }
  });

  app.post("/api/students", async (req, res) => {
    try {
      const studentData = insertStudentSchema.parse(req.body);
      const student = await storage.createStudent(studentData);
      res.status(201).json(student);
    } catch (error: any) {
      res.status(400).json({ message: error.message || "Invalid student data" });
    }
  });

  app.put("/api/students/:id", async (req, res) => {
    try {
      const updates = insertStudentSchema.partial().parse(req.body);
      const student = await storage.updateStudent(req.params.id, updates);
      if (!student) {
        return res.status(404).json({ message: "Student not found" });
      }
      res.json(student);
    } catch (error: any) {
      res.status(400).json({ message: error.message || "Invalid update data" });
    }
  });

  app.delete("/api/students/:id", async (req, res) => {
    try {
      const deleted = await storage.deleteStudent(req.params.id);
      if (!deleted) {
        return res.status(404).json({ message: "Student not found" });
      }
      res.status(204).send();
    } catch (error) {
      res.status(500).json({ message: "Failed to delete student" });
    }
  });

  // Classes routes
  app.get("/api/classes", async (req, res) => {
    try {
      const classes = await storage.getClasses();
      res.json(classes);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch classes" });
    }
  });

  app.get("/api/classes/current", async (req, res) => {
    try {
      const currentClass = await storage.getCurrentClass();
      res.json(currentClass);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch current class" });
    }
  });

  app.post("/api/classes", async (req, res) => {
    try {
      const classData = insertClassSchema.parse(req.body);
      const newClass = await storage.createClass(classData);
      res.status(201).json(newClass);
    } catch (error: any) {
      res.status(400).json({ message: error.message || "Invalid class data" });
    }
  });

  app.put("/api/classes/:id", async (req, res) => {
    try {
      const updates = insertClassSchema.partial().parse(req.body);
      const updatedClass = await storage.updateClass(req.params.id, updates);
      if (!updatedClass) {
        return res.status(404).json({ message: "Class not found" });
      }
      res.json(updatedClass);
    } catch (error: any) {
      res.status(400).json({ message: error.message || "Invalid update data" });
    }
  });

  app.post("/api/classes/:id/start", async (req, res) => {
    try {
      const startedClass = await storage.startClass(req.params.id);
      if (!startedClass) {
        return res.status(404).json({ message: "Class not found" });
      }
      res.json(startedClass);
    } catch (error) {
      res.status(500).json({ message: "Failed to start class" });
    }
  });

  app.post("/api/classes/:id/end", async (req, res) => {
    try {
      const endedClass = await storage.endClass(req.params.id);
      if (!endedClass) {
        return res.status(404).json({ message: "Class not found" });
      }
      res.json(endedClass);
    } catch (error) {
      res.status(500).json({ message: "Failed to end class" });
    }
  });

  // Attendance routes
  app.get("/api/attendance", async (req, res) => {
    try {
      const classId = req.query.classId as string;
      const records = await storage.getAttendanceRecords(classId);
      res.json(records);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch attendance records" });
    }
  });

  app.post("/api/attendance", async (req, res) => {
    try {
      const recordData = insertAttendanceRecordSchema.parse(req.body);
      const record = await storage.createAttendanceRecord(recordData);
      res.status(201).json(record);
    } catch (error: any) {
      res.status(400).json({ message: error.message || "Invalid attendance data" });
    }
  });

  app.put("/api/attendance/:id", async (req, res) => {
    try {
      const updates = insertAttendanceRecordSchema.partial().parse(req.body);
      const record = await storage.updateAttendanceRecord(req.params.id, updates);
      if (!record) {
        return res.status(404).json({ message: "Attendance record not found" });
      }
      res.json(record);
    } catch (error: any) {
      res.status(400).json({ message: error.message || "Invalid update data" });
    }
  });

  // Behavior warnings routes
  app.get("/api/warnings", async (req, res) => {
    try {
      const classId = req.query.classId as string;
      const isActive = req.query.isActive === "true";
      const warnings = await storage.getBehaviorWarnings(classId, isActive);
      res.json(warnings);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch warnings" });
    }
  });

  app.post("/api/warnings", async (req, res) => {
    try {
      const warningData = insertBehaviorWarningSchema.parse(req.body);
      const warning = await storage.createBehaviorWarning(warningData);
      res.status(201).json(warning);
    } catch (error: any) {
      res.status(400).json({ message: error.message || "Invalid warning data" });
    }
  });

  app.put("/api/warnings/:id/dismiss", async (req, res) => {
    try {
      const dismissed = await storage.dismissBehaviorWarning(req.params.id);
      if (!dismissed) {
        return res.status(404).json({ message: "Warning not found" });
      }
      res.status(204).send();
    } catch (error) {
      res.status(500).json({ message: "Failed to dismiss warning" });
    }
  });

  // Face detection routes
  app.post("/api/face-detections", async (req, res) => {
    try {
      const detectionData = insertFaceDetectionSchema.parse(req.body);
      const detection = await storage.createFaceDetection(detectionData);
      res.status(201).json(detection);
    } catch (error: any) {
      res.status(400).json({ message: error.message || "Invalid detection data" });
    }
  });

  app.get("/api/face-detections/recent", async (req, res) => {
    try {
      const classId = req.query.classId as string;
      const minutes = parseInt(req.query.minutes as string) || 5;
      if (!classId) {
        return res.status(400).json({ message: "classId is required" });
      }
      const detections = await storage.getRecentFaceDetections(classId, minutes);
      res.json(detections);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch recent detections" });
    }
  });

  // Teachers routes
  app.get("/api/teachers", async (req, res) => {
    try {
      const teachers = await storage.getTeachers();
      res.json(teachers);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch teachers" });
    }
  });

  app.get("/api/teachers/:id", async (req, res) => {
    try {
      const teacher = await storage.getTeacher(req.params.id);
      if (!teacher) {
        return res.status(404).json({ message: "Teacher not found" });
      }
      res.json(teacher);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch teacher" });
    }
  });

  app.post("/api/teachers", async (req, res) => {
    try {
      const teacherData = insertTeacherSchema.parse(req.body);
      const teacher = await storage.createTeacher(teacherData);
      res.status(201).json(teacher);
    } catch (error: any) {
      res.status(400).json({ message: error.errors?.[0]?.message || "Invalid teacher data" });
    }
  });

  app.put("/api/teachers/:id", async (req, res) => {
    try {
      const teacherData = insertTeacherSchema.parse(req.body);
      const teacher = await storage.updateTeacher(req.params.id, teacherData);
      if (!teacher) {
        return res.status(404).json({ message: "Teacher not found" });
      }
      res.json(teacher);
    } catch (error: any) {
      res.status(400).json({ message: error.errors?.[0]?.message || "Invalid teacher data" });
    }
  });

  app.delete("/api/teachers/:id", async (req, res) => {
    try {
      const deleted = await storage.deleteTeacher(req.params.id);
      if (!deleted) {
        return res.status(404).json({ message: "Teacher not found" });
      }
      res.status(204).send();
    } catch (error) {
      res.status(500).json({ message: "Failed to delete teacher" });
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

server/storage.ts.content:
import { 
  type Student, 
  type InsertStudent,
  type Class,
  type InsertClass,
  type AttendanceRecord,
  type InsertAttendanceRecord,
  type BehaviorWarning,
  type InsertBehaviorWarning,
  type FaceDetection,
  type InsertFaceDetection,
  type Teacher,
  type InsertTeacher
} from "@shared/schema";
import { randomUUID } from "crypto";

export interface IStorage {
  // Students
  getStudents(): Promise<Student[]>;
  getStudent(id: string): Promise<Student | undefined>;
  getStudentByStudentId(studentId: string): Promise<Student | undefined>;
  createStudent(student: InsertStudent): Promise<Student>;
  updateStudent(id: string, updates: Partial<Student>): Promise<Student | undefined>;
  deleteStudent(id: string): Promise<boolean>;

  // Classes
  getClasses(): Promise<Class[]>;
  getClass(id: string): Promise<Class | undefined>;
  getCurrentClass(): Promise<Class | undefined>;
  createClass(classData: InsertClass): Promise<Class>;
  updateClass(id: string, updates: Partial<Class>): Promise<Class | undefined>;
  startClass(id: string): Promise<Class | undefined>;
  endClass(id: string): Promise<Class | undefined>;

  // Attendance
  getAttendanceRecords(classId?: string): Promise<AttendanceRecord[]>;
  getAttendanceRecord(studentId: string, classId: string): Promise<AttendanceRecord | undefined>;
  createAttendanceRecord(record: InsertAttendanceRecord): Promise<AttendanceRecord>;
  updateAttendanceRecord(id: string, updates: Partial<AttendanceRecord>): Promise<AttendanceRecord | undefined>;

  // Behavior Warnings
  getBehaviorWarnings(classId?: string, isActive?: boolean): Promise<BehaviorWarning[]>;
  createBehaviorWarning(warning: InsertBehaviorWarning): Promise<BehaviorWarning>;
  dismissBehaviorWarning(id: string): Promise<boolean>;

  // Face Detections
  createFaceDetection(detection: InsertFaceDetection): Promise<FaceDetection>;
  getRecentFaceDetections(classId: string, minutes?: number): Promise<FaceDetection[]>;

  // Teachers
  getTeachers(): Promise<Teacher[]>;
  getTeacher(id: string): Promise<Teacher | undefined>;
  createTeacher(teacher: InsertTeacher): Promise<Teacher>;
  updateTeacher(id: string, updates: Partial<Teacher>): Promise<Teacher | undefined>;
  deleteTeacher(id: string): Promise<boolean>;
}

export class MemStorage implements IStorage {
  private students: Map<string, Student> = new Map();
  private classes: Map<string, Class> = new Map();
  private attendanceRecords: Map<string, AttendanceRecord> = new Map();
  private behaviorWarnings: Map<string, BehaviorWarning> = new Map();
  private faceDetections: Map<string, FaceDetection> = new Map();
  private teachers: Map<string, Teacher> = new Map();

  constructor() {
    // Start with empty storage - no demo data
  }

  // Students
  async getStudents(): Promise<Student[]> {
    return Array.from(this.students.values());
  }

  async getStudent(id: string): Promise<Student | undefined> {
    return this.students.get(id);
  }

  async getStudentByStudentId(studentId: string): Promise<Student | undefined> {
    return Array.from(this.students.values()).find(s => s.studentId === studentId);
  }

  async createStudent(insertStudent: InsertStudent): Promise<Student> {
    const id = randomUUID();
    const student: Student = {
      ...insertStudent,
      id,
      createdAt: new Date()
    };
    this.students.set(id, student);
    return student;
  }

  async updateStudent(id: string, updates: Partial<Student>): Promise<Student | undefined> {
    const existing = this.students.get(id);
    if (!existing) return undefined;

    const updated = { ...existing, ...updates };
    this.students.set(id, updated);
    return updated;
  }

  async deleteStudent(id: string): Promise<boolean> {
    return this.students.delete(id);
  }

  // Classes
  async getClasses(): Promise<Class[]> {
    return Array.from(this.classes.values());
  }

  async getClass(id: string): Promise<Class | undefined> {
    return this.classes.get(id);
  }

  async getCurrentClass(): Promise<Class | undefined> {
    return Array.from(this.classes.values()).find(c => c.isActive);
  }

  async createClass(insertClass: InsertClass): Promise<Class> {
    const id = randomUUID();
    const classData: Class = {
      ...insertClass,
      id,
      createdAt: new Date()
    };
    this.classes.set(id, classData);
    return classData;
  }

  async updateClass(id: string, updates: Partial<Class>): Promise<Class | undefined> {
    const existing = this.classes.get(id);
    if (!existing) return undefined;

    const updated = { ...existing, ...updates };
    this.classes.set(id, updated);
    return updated;
  }

  async startClass(id: string): Promise<Class | undefined> {
    const existing = this.classes.get(id);
    if (!existing) return undefined;

    // End any other active classes
    for (const [cId, c] of this.classes) {
      if (c.isActive && cId !== id) {
        this.classes.set(cId, { ...c, isActive: false, endedAt: new Date() });
      }
    }

    const updated = { 
      ...existing, 
      isActive: true, 
      startedAt: new Date(),
      endedAt: null 
    };
    this.classes.set(id, updated);
    return updated;
  }

  async endClass(id: string): Promise<Class | undefined> {
    const existing = this.classes.get(id);
    if (!existing) return undefined;

    const updated = { 
      ...existing, 
      isActive: false, 
      endedAt: new Date() 
    };
    this.classes.set(id, updated);
    return updated;
  }

  // Attendance
  async getAttendanceRecords(classId?: string): Promise<AttendanceRecord[]> {
    const records = Array.from(this.attendanceRecords.values());
    return classId ? records.filter(r => r.classId === classId) : records;
  }

  async getAttendanceRecord(studentId: string, classId: string): Promise<AttendanceRecord | undefined> {
    return Array.from(this.attendanceRecords.values())
      .find(r => r.studentId === studentId && r.classId === classId);
  }

  async createAttendanceRecord(insertRecord: InsertAttendanceRecord): Promise<AttendanceRecord> {
    const id = randomUUID();
    const record: AttendanceRecord = {
      ...insertRecord,
      id,
      createdAt: new Date()
    };
    this.attendanceRecords.set(id, record);
    return record;
  }

  async updateAttendanceRecord(id: string, updates: Partial<AttendanceRecord>): Promise<AttendanceRecord | undefined> {
    const existing = this.attendanceRecords.get(id);
    if (!existing) return undefined;

    const updated = { ...existing, ...updates };
    this.attendanceRecords.set(id, updated);
    return updated;
  }

  // Behavior Warnings
  async getBehaviorWarnings(classId?: string, isActive?: boolean): Promise<BehaviorWarning[]> {
    let warnings = Array.from(this.behaviorWarnings.values());
    if (classId) warnings = warnings.filter(w => w.classId === classId);
    if (isActive !== undefined) warnings = warnings.filter(w => w.isActive === isActive);
    return warnings;
  }

  async createBehaviorWarning(insertWarning: InsertBehaviorWarning): Promise<BehaviorWarning> {
    const id = randomUUID();
    const warning: BehaviorWarning = {
      ...insertWarning,
      id,
      createdAt: new Date()
    };
    this.behaviorWarnings.set(id, warning);
    return warning;
  }

  async dismissBehaviorWarning(id: string): Promise<boolean> {
    const warning = this.behaviorWarnings.get(id);
    if (!warning) return false;

    this.behaviorWarnings.set(id, { ...warning, isActive: false });
    return true;
  }

  // Face Detections
  async createFaceDetection(insertDetection: InsertFaceDetection): Promise<FaceDetection> {
    const id = randomUUID();
    const detection: FaceDetection = {
      ...insertDetection,
      id,
      timestamp: new Date()
    };
    this.faceDetections.set(id, detection);
    return detection;
  }

  async getRecentFaceDetections(classId: string, minutes: number = 5): Promise<FaceDetection[]> {
    const cutoff = new Date(Date.now() - minutes * 60 * 1000);
    return Array.from(this.faceDetections.values())
      .filter(d => d.classId === classId && d.timestamp && d.timestamp > cutoff);
  }

  // Teachers
  async getTeachers(): Promise<Teacher[]> {
    return Array.from(this.teachers.values());
  }

  async getTeacher(id: string): Promise<Teacher | undefined> {
    return this.teachers.get(id);
  }

  async createTeacher(insertTeacher: InsertTeacher): Promise<Teacher> {
    const id = randomUUID();
    const teacher: Teacher = {
      ...insertTeacher,
      id,
      createdAt: new Date()
    };
    this.teachers.set(id, teacher);
    return teacher;
  }

  async updateTeacher(id: string, updates: Partial<Teacher>): Promise<Teacher | undefined> {
    const existing = this.teachers.get(id);
    if (!existing) return undefined;

    const updated = { ...existing, ...updates };
    this.teachers.set(id, updated);
    return updated;
  }

  async deleteTeacher(id: string): Promise<boolean> {
    return this.teachers.delete(id);
  }
}

export const storage = new MemStorage();

